import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import authService from "../../../services/auth.service";

const loginByEmailAndPassword = createAsyncThunk(
  "auth/loginByEmailAndPassword",
  async (email, password) => {
    const response = await authService.login(email, password);
    return response.data;
  }
);

const initialState = {
  isLoading: false,
  isAuth: false,
  error: "",
};

const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {},
  extraReducers: {
    [loginByEmailAndPassword.pending]: (state, action) => {
      state.isLoading = true;
      state.error = "";
      state.isAuth = false;
    },
    [loginByEmailAndPassword.rejected]: (state, action) => {
      state.isLoading = false;
      state.isAuth = false;
      state.error = "Email or Password Invalid.";
    },
    [loginByEmailAndPassword.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.error = "";
      state.isAuth = true;

      console.log(action);
    },
  },
});

export const { login } = authSlice.actions;
export default authSlice.reducer;
